import React, { Component } from 'react';
import './App.css';
import './css/index.css';
import AppContent from './components/app-content';

class App extends Component {
  constructor() {
    super();
    this.state = {
      userinfo: {
        username: 'Pedro Pacheco',
        photo: 'https://avatars3.githubusercontent.com/u/35742444?v=4',
        login: 'Pacheck',
        repos: 10,
        followers: 11,
        following: 14,
      },
      repos: [
        {
          name: 'Repo',
          link: '#',
        },
      ],
      starred: [
        {
          name: 'Starred',
          link: '#',
        },
      ],
    };
  }

  handleSearch(e) {
    const value = e.target.value;
    const keyCode = e.which || e.keyCode;
    const ENTER = 13;
    if (keyCode === ENTER) {
      ajax()
        .get(`https://api.github.com/users/${value}`)
        .then((result) => {
          console.log(result);
        })
        .catch((err) => console.error(`Não encontrado ! ${err}`));
    }
  }

  render() {
    return (
      <AppContent
        userinfo={this.state.userinfo}
        repos={this.state.repos}
        starred={this.state.starred}
        handleSearch={(e) => this.handleSearch(e)}
      />
    );
  }
}

export default App;
